html:
{%- from "templates/print_formats/gboq_standard_macros.html" import render_field -%}
{%- from "templates/print_formats/standard_macros.html" import add_header,add_footer -%}




{% set cnt = [0] %}
<div{% if print_settings.repeat_header_footer %} id="header-html" class="hidden-pdf"{% endif %}>
		{{ add_header(1, layout|len, doc, letter_head, no_letterhead, footer,print_settings) }}
	</div>

{% for page in layout %}
<div class="page-break">

    {% for section in page %}
    <div class="row section-break">
		{%- if doc._line_breaks and loop.index != 1 -%}<hr>{%- endif -%}
		{%- if doc._show_section_headings and section.label -%}
		<h4 class='col-sm-12'>{{ section.label }}</h4>
		{%- endif -%}
       {% for column in section.columns %}
			<div class="col-xs-{{ (12 / section.columns|len)|int }} column-break">
            {% for df in column.fields %}
{{ render_field(df, doc,cnt) }}
            {% endfor %}
			</div>
        {% endfor %}
    </div>
    {% endfor %}

</div>
{% endfor %}

		{{ add_footer(layout|len, doc, no_letterhead, footer, print_settings) }}
